function bgChangeColor(){var o=$("body"),c=$(".bg__color.blue"),n=$(".bg__color.red"),l=$(".bg__color.yellow"),i=$(".bg__color.light"),r=$(".bg__color.dark");c.on("click",function(){o.css("background-color","#2980D9")}),n.on("click",function(){o.css("background-color","#E74C3C")}),l.on("click",function(){o.css("background-color","#F1C40F")}),i.on("click",function(){o.css("background-color","#F7F7F7")}),r.on("click",function(){o.css("background-color","#433F3F")})}function detailsView(){var o=$(".palette"),c=$(".palette__details");o.on("click",function(){c.removeClass("active"),$(this).next(c).toggleClass("active")})}$(document).ready(function(){bgChangeColor(),detailsView()});