function bgChangeColor(){var o=$(".content"),c=$(".bg__color.blue"),n=$(".bg__color.red"),l=$(".bg__color.yellow"),r=$(".bg__color.light"),g=$(".bg__color.dark");c.on("click",function(){o.css("background-color","#2980D9")}),n.on("click",function(){o.css("background-color","#E74C3C")}),l.on("click",function(){o.css("background-color","#F1C40F")}),r.on("click",function(){o.css("background-color","#F7F7F7")}),g.on("click",function(){o.css("background-color","#433F3F")})}$(document).ready(function(){bgChangeColor()});